import React from 'react';
import SearchForm from './components/SearchForm';
import ResultsList from './components/ResultsList';
import './App.css';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      results: [],
      loading: false,
      error: null,
      suggestions: [
        'cancer research',
        'COVID-19 vaccine',
        'machine learning in healthcare',
        'genetic engineering',
        'neuroscience'
      ]
    };
  }

  handleSearch = async (query) => {
    this.setState({ loading: true, error: null });
    
    try {
      const response = await fetch('http://localhost:8081/api/search', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ query }),
      });

      if (!response.ok) {
        throw new Error('Search failed');
      }

      const data = await response.json();
      this.setState({ results: data.results, loading: false });
    } catch (error) {
      this.setState({ error: error.message, loading: false });
    }
  };

  render() {
    const { results, loading, error, suggestions } = this.state;

    return (
      <div className="App">
        <header className="App-header">
          <h1>PubMed Explorer</h1>
          <p className="subtitle">Search and analyze research papers from PubMed</p>
        </header>
        <main className="App-main">
          <section className="hero">
            <h1>PubMed Explorer</h1>
            <p className="subtitle">Discover and analyze biomedical research papers</p>
            
            <div className="pubmed-info">
              <h2>About PubMed</h2>
              <p>PubMed is a free search engine accessing primarily the MEDLINE database of references and abstracts on life sciences and biomedical topics. The United States National Library of Medicine (NLM) at the National Institutes of Health maintains the database as part of the Entrez system of information retrieval.</p>
              <div className="pubmed-features">
                <div className="feature">
                  <h3>Comprehensive Database</h3>
                  <p>Access to over 35 million citations for biomedical literature</p>
                </div>
                <div className="feature">
                  <h3>Trusted Source</h3>
                  <p>Maintained by the National Library of Medicine</p>
                </div>
                <div className="feature">
                  <h3>Free Access</h3>
                  <p>Open access to biomedical research information</p>
                </div>
              </div>
            </div>

            <div className="search-container">
              <SearchForm onSearch={this.handleSearch} />
              <div className="suggestions">
                <h3>Popular Searches:</h3>
                <div className="suggestion-tags">
                  {suggestions.map((suggestion, index) => (
                    <button
                      key={index}
                      className="suggestion-tag"
                      onClick={() => this.handleSearch(suggestion)}
                    >
                      {suggestion}
                    </button>
                  ))}
                </div>
              </div>
            </div>
          </section>
          {error && <div className="error-message">{error}</div>}
          <ResultsList results={results} loading={loading} />
        </main>
        <section className="about-pubmed">
          <h2>About PubMed</h2>
          <div className="about-content">
            <div className="about-text">
              <p>PubMed is a free resource developed and maintained by the National Center for Biotechnology Information (NCBI) at the U.S. National Library of Medicine (NLM), located at the National Institutes of Health (NIH).</p>
              
              <h3>Educational Value</h3>
              <ul>
                <li>Access to peer-reviewed biomedical literature</li>
                <li>Comprehensive coverage of life sciences and medicine</li>
                <li>Links to full-text articles when available</li>
                <li>Advanced search capabilities for research</li>
                <li>Regular updates with new publications</li>
              </ul>

              <h3>Key Features</h3>
              <ul>
                <li>Over 35 million citations from MEDLINE and other sources</li>
                <li>Links to full-text articles from publishers and PubMed Central</li>
                <li>Advanced search tools and filters</li>
                <li>MyNCBI for saving searches and creating collections</li>
                <li>Clinical queries for evidence-based medicine</li>
              </ul>
            </div>
            <div className="about-image">
              <img src="https://www.ncbi.nlm.nih.gov/corehtml/pmc/pmcgifs/pmc-logo-share.png" alt="PubMed Logo" />
            </div>
          </div>
        </section>
        <footer className="App-footer">
          <p>Powered by PubMed API | National Library of Medicine</p>
        </footer>
      </div>
    );
  }
}

export default App;
